tinymce.PluginManager.add('filemanager', function(editor) {
    function openmanager() {
        var win, data, dom = editor.dom,
            imgElm = editor.selection.getNode();
        var width, height, imageListCtrl;
        win = editor.windowManager.open({
            title: 'File Manager',
            file: tinyMCE.baseURL + '/plugins/filemanager/dialog.php?editor=' + editor.id + '&lang=' + tinymce.settings.language + '&subfolder=' + tinymce.settings.subfolder,
            filetype: 'all',
            classes: 'filemanager',
            width: _width(),
            height: _height(),
            inline: 1
        })
    }
    function _width(){
        var w = $(window).width();
        var width = w;
        if(w >= 900){
            width= 900;
        }

        return width;
    }

    function _height(){
        var h = $(window).height();
        var height = h - 30;
        if(h >= 600){
            height= 600;
        }

        return height;
    }
    editor.addButton('filemanager', {
        icon: 'browse',
        tooltip: 'Insert file',
        onclick: openmanager,
        stateSelector: 'img:not([data-mce-object])'
    });
    editor.addMenuItem('filemanager', {
        icon: 'browse',
        text: 'Insert file',
        onclick: openmanager,
        context: 'insert',
        prependToContext: true
    })
});